select
from issues i inner join rules r on r.id=i.rule_id inner join projects p on p.id=i.component_id inner join projects root on root.id=i.root_component_id where i.kee in
#{key}

select
from issues i inner join rules r on r.id=i.rule_id inner join projects p on p.id=i.component_id inner join projects root on root.id=i.root_component_id
and i.action_plan_key=#{action_plan}

SELECT
FROM issues i INNER JOIN projects p on p.id=i.component_id INNER JOIN rules r on r.id=i.rule_id WHERE p.kee=#{componentKey} AND i.resolution IS NULL
AND i.issue_creation_date >= #{createdAt}
